*,
*::after,
*::before {
  padding: 0;
  margin: 0;
  box-sizing: border-box;
}

body {
  padding: 1.2rem;
  box-sizing: border-box;
}

html {
  /* default browser font size : 16px  */
  /* font size prefer 10px so to do easy calculation [1rem = 10px] */
  font-size: 62.5%;

  /* force inherit as text are inherited by default to inherit other we force them */
  box-sizing: inherit;
}

