body {
  font-family: "Lato", sans-serif;
  color: $color-black-m;
  font-weight: 400;
  line-height: $default-line-height;
}

.heading-primary {
  text-transform: uppercase;
  // color: $color-white;
  /* Hide the shaking of animated element in the end */
  backface-visibility: hidden;
  font-size: 4.4rem;

  &--main,
  &-sub {
    display: block;
  }

  &--main {
    font-size: 6.2rem;
    font-weight: 400;
    letter-spacing: 4.8rem;
    animation-name: moveLeftIn;
    animation-duration: 2s;
    animation-timing-function: ease-out;
    /* 
animation-delay: 10s;
animation-timing-function: ease-in;
*/
  }

  &--sub {
    font-size: 2.2rem;
    font-weight: 400;
    letter-spacing: 0.6rem;
    animation: moveRightIn 2s ease-out;
    letter-spacing: 2.2rem;
    font-weight: 600;
  }
}

.heading-secondary,
.heading-tertiary,
.heading-quad {
  font-weight: 700;
  text-transform: uppercase;
  color: $color-black-d;
  line-height: 1.6;
}

.heading-secondary {
  font-size: 3.6rem;

  letter-spacing: 1.75px;
  transition: all 0.3s;
  background: linear-gradient(to right, $color-primary, $color-primary--dark);

  -webkit-background-clip: text;
  background-clip: text;
  color: transparent;

  // like box-shadow:
  text-shadow: 0.5rem 1rem 2rem rgba($color-black, $alpha: 0.2);

  &:hover {
    transform: skewY(2deg) scale(1.1);
  }
}

.heading-tertiary {
  letter-spacing: 0.5px;
  font-size: 2.4rem;
