    opacity: 0;
    visibility: hidden;
  }

  & div:nth-child(3) {
    display: flex;
    justify-content: space-around;
  }

  &__radio-input {
    display: none;
  }

  &__radio-label {
    cursor: pointer;
  }
  // Span element -> inline element
  &__radio-btn {
    display: inline-block;
    transform: translateY(0.7rem);

    // To set height, width, margin, padding in inline element we need to make the inline element a block element
    height: 3rem;
    width: 3rem;
    border: 6px solid $color-primary;
    border-radius: 50%;
    position: relative;

    &::after {
      content: "";

      // To set height and width in inline element we need to make inline element into block element
      display: inline-block;
      height: 1.5rem;
      width: 1.5rem;
      background-color: $color-primary;
      border-radius: 50%;
      @include postion-center;
      @include hide;
      transition: opacity 0.5s;
    }
  }

  // Sibling element's child pseudo element
  &__radio-input:checked + &__radio-label &__radio-btn::after {
    @include show;

  }
}

